INSERT INTO `foundation` VALUES (2,'Quỹ Từ Thiện & Bảo Trợ Xã Hội Trăng Khuyết','Trăng Khuyết là tổ chức từ thiện nhân đạo, hoạt động độc lập, phi tôn giáo và phi lợi nhuận trên toàn lãnh thổ Việt Nam, nhằm kết nối cộng đồng và thực hiện các hoạt động cứu trợ trực tiếp liên quan đến: bữa ăn, chỗ ở, chăm sóc sức khỏe - tinh thần cho các cụ già không nơi nương tựa và trẻ em bị bỏ rơi.','abc1@gmail.com','Enable'),(3,'Sức Mạnh 2000 - Ánh sáng núi rừng','Quỹ Sức mạnh 2000 thuộc hệ sinh thái Nuôi Em, được thành lập để nâng cao chất lượng Cơ sở vật chất cho các em nhỏ khó khăn vùng cao.Mỗi người 2.000 đồng/ngày, chúng tôi đã xây dựng trong 10 năm qua','abc2@gmail.com','Enable'),(4,'Quỹ Từ Thiện Hoa Chia Sẻ','Quỹ xã hội – từ thiện được thành lập tự nguyện, không vì lợi nhuận, trên nguyên tắc tự tạo vốn. Quỹ hoạt động nhằm mục đích xã hội – từ thiện, ưu tiên giúp đỡ các gia đình khó khăn, có công với cách mạng và hỗ trợ, khuyến khích phát triển văn hóa, giáo dục, y tế, thể dục thể thao, khoa học và các mục đích phát triển cộng đồng phù hợp quy định của pháp luật.','abc3@gmail.com','Enable'),(5,'Quỹ Hy Vọng - Hope Foundation','Ra đời năm 2017, Quỹ Hy Vọng là một quỹ xã hội - từ thiện hoạt động vì cộng đồng, không vì lợi nhuận, được vận hành bởi Báo điện tử VnExpress và Công ty cổ phần FPT. Quỹ Hy vọng theo đuổi hai mục tiêu: hỗ trợ các hoàn cảnh khó khăn và tạo động lực phát triển.','abc4@gmail.com','Enable'),(6,'VinaCapital Foundation','Tổ chức VinaCapital Foundation (VCF) hoạt động với sứ mệnh trao quyền cho trẻ em và phụ nữ Việt Nam thông qua các chương trình y tế và giáo dục.','abc5@gmail.com','Enable'),(7,'Viện Nghiên cứu Quản lý Phát triển bền vững (MSD)','Là một tổ chức phi chính phủ Việt Nam, MSD nỗ lực hành động thúc đẩy việc thực hiện quyền của các nhóm cộng đồng bị lề hoá, dễ bị tổn thương. MSD cũng là tổ chức đáp ứng nhu cầu và bảo vệ các đối tượng có hoàn cảnh khó khăn, thông qua các dự án và hỗ trợ trẻ em, thanh thiếu niên, phụ nữ, người vô gia cư, người nhập cư và người khuyết tật tại Việt Nam.','abc6@gmail.com','Enable'),(8,'Trung tâm Tương Lai','Trung tâm Tương Lai là tổ chức chuyên hoạt động để hỗ trợ giáo dục và bảo vệ trẻ em và thanh thiếu niên.','abc7@gmail.com','Enable'),(9,'Quỹ Bảo Trợ Thú Cưng Sài Gòn','SPP - Quỹ Bảo trợ thú cưng Sài Gòn mong muốn nhận được sự đóng góp của các nhà hảo tâm để cứu hộ và chăm sóc cho những bé chó mèo bị đánh đập và bỏ rơi do bệnh tật. Sự chung tay giúp đỡ của mọi người là niềm động lực để SPP vững bước trên hành trình bảo trợ các bé.','phamtuanvu12345@gmail.com','Enable'),(3665,'Trung Tâm Hỗ Trợ Học Sinh Sinh Viên TP Hồ Chí Minh','Hỗ trợ học sinh sinh phiên tìm nhà trọ quán ăn giá rẻ','vu.pt172@gmail.com','Enable'),(3666,'Operation Smile','Operation Smile mang lại nụ cười và thay đổi cuộc sống của những trẻ em bị dị tật bẩm sinh trên khuôn mặt ở Việt Nam và khắp thế giới.','operationsmile123@gmail.com','Enable'),(3667,'Sika Vietnam','','sikavietnam@gmail.com','Enable'),(3668,'Anh Chị Nuôi Dự án Nuôi Em','Xuất phát điểm từ việc xây trường mà trẻ vẫn bỏ học vì thiếu ăn nên dự án đã tiến hành Nuôi Cơm vùng cao, cụ thể, mỗi bữa cơm là 8500đ và dự án lo việc nấu đồ ăn (thông qua thầy cô) cơm thì bố mẹ các cháu nấu cho các cháu đem đi (một số địa phương đặc thù thì thầy cô giáo nấu cơm ). Dự án này thực hiện từ 2014, tuy nhiên bùng nổ vào mùa 2018-2019 khi đưa ra mô hình MỘT NGƯỜI - NUÔI MỘT CHÁU  mỗi tháng 150,000, mỗi năm 1,350,000 đặc thù là tính Cá Nhân Hóa (Mỗi người nuôi đều biết mặt mũi, thông tin, sđt bố mẹ thầy cô già làng trưởng bản, hiệu trưởng, Phòng giáo dục và Được lên thăm các cháu 3 lần/năm) + Tính lan tỏa + Tính bền vững ','anhchinuoiduannuoiem@gmail.com','Enable');
/*!40000 ALTER TABLE `foundation` ENABLE KEYS */;
